import React, {Component} from "react";
import LineChart from "../charts/LineChart";

class IndexChart extends Component {
    constructor(props){
        super(props);
        this.state = {
            chartData:{}
        }
    }

    componentWillMount(){
        this.getChartData();
    }

    getChartData() {
        // Ajax calls here
        this.setState({
            chartData:{
                labels: ['Boston', 'Worcester', 'Springfield', 'Lowell', 'Cambridge', 'New Bedford'],
                datasets:[
                    {
                        label:'Kurs rzeczywisty',
                        data:[
                            617594,
                            181045,
                            153060,
                            106519,
                            105162,
                            95072
                        ],
                        borderColor: "#3cba9f",
                        backgroundColor: "#cd0316",
                        fill: false
                    }
                ]
            }
        });
    }



    render() {
        return(
            <div>
                <h2>Witaj w aplikacji wspomagającej w predykcji kursów par walutowych!</h2>
                <LineChart chartData={this.state.chartData} currencyPair="EUR/USD" legendPosition="bottom"/>
            </div>
        );
    }
}

export default IndexChart;
