import React, {Component} from "react";
import LineChart from "../charts/LineChart";
import {Col, Container, Row} from "reactstrap";
import LineChartCard from "./components/LineChartCard";

class IndexChart extends Component {
    constructor(props) {
        super(props);
        this.state = {
            chartData: {}
        }
    }

    componentWillMount() {
        this.getChartData();
    }

    getChartData() {
        // Ajax calls here
        this.setState({
            chartData: {
                labels: ['Boston', 'Worcester', 'Springfield', 'Lowell', 'Cambridge', 'New Bedford'],
                datasets: [
                    {
                        label: 'Kurs rzeczywisty',
                        data: [
                            617594,
                            181045,
                            153060,
                            106519,
                            105162,
                            95072
                        ],
                        borderColor: "#3cba9f",
                        backgroundColor: "#cd0316",
                        pointHoverBorderWidth: 2,
                        fill: false
                    }
                ]
            }
        });
    }

    render() {
        return (
            <Container className="dashboard">
                <Row>
                    <Col md={12}>
                        <h3 className="page-title">Kurs pary walutowej EUR/USD - wykres LineChart</h3>
                    </Col>
                </Row>
                <Row>
                    <LineChartCard/>
                </Row>
                <div>
                    <LineChart chartData={this.state.chartData} currencyPair="EUR/USD" legendPosition="bottom"/>
                </div>
            </Container>
        );
    }
}

export default IndexChart;
